import requests

BASE_URL = "http://192.168.1.50:8000/ISAPI"

def get_deploy_info_capabilities():
    url = f"{BASE_URL}/AccessControl/DeployInfo/capabilities"
    try:
        response = requests.get(url)
        response.raise_for_status()  # Raise an error for bad responses
        return response.json()
    except requests.exceptions.RequestException as e:
        print(f"An error occurred: {e}")

if __name__ == "__main__":
    capabilities = get_deploy_info_capabilities()
    print("Deploy Info Capabilities:", capabilities)
